const minutesElement = document.getElementById('minutes');
const secondsElement = document.getElementById('seconds');
const startButton = document.getElementById('startButton');
const resetButton = document.getElementById('resetButton');
const estadoElement = document.getElementById('estado');

const slider = document.getElementById('slider');
const increaseButton = document.getElementById('increaseButton');
const decreaseButton = document.getElementById('decreaseButton');

let intervalId;
let prevSeconds = 5;
let totalSeconds = 60  + prevSeconds; // 1 minuto = 60 segundos
let currentSeconds = totalSeconds;
let estado = 'Preparado';

// Got this from strackoverflow (https://stackoverflow.com/questions/879152/how-do-i-make-javascript-beep)
function beep() {
    var snd = new Audio("data:audio/wav;base64,");  
    snd.play();
}

function updateDisplay() {
    const minutes = Math.floor(currentSeconds / 60);
    const seconds = currentSeconds % 60;

    minutesElement.textContent = String(minutes).padStart(2, '0');
    secondsElement.textContent = String(seconds).padStart(2, '0');
}

function startTimer() {
    intervalId = setInterval(() => {
        currentSeconds--;
        updateDisplay();

        if (currentSeconds % 60 === 10) {
            beep(); // Llama a tu función beep()
        }
        if (currentSeconds % 60 === 3) {
            beep(); // Llama a tu función beep()
        }
        if (currentSeconds % 60 === 2) {
            beep(); // Llama a tu función beep()
        }
        if (currentSeconds % 60 === 1) {
            beep(); // Llama a tu función beep()
        }
        if (currentSeconds % 60 === 0) { // Cada vez que inicia un minuto
            beep(); // Llama a tu función beep()
			beep(); // Llama a tu función beep()
			beep(); // Llama a tu función beep()
			beep(); // Llama a tu función beep()
			beep(); // Llama a tu función beep()
        }
		
        if (currentSeconds === 0) {
            clearInterval(intervalId);
            currentSeconds = totalSeconds;
            estado = 'Descanso';
            estadoElement.textContent = estado;
			
			// Habilitar el botón de reinicio
            resetButton.disabled = false;
			slider.disabled = false;
			stopButton.disabled = true;
			startButton.textContent = "Iniciar";
        }
    }, 1000); // 1 segundo = 1000 milisegundos

    startButton.disabled = true;
	startButton.textContent = "Reanudar";
    resetButton.disabled = false;
	stopButton.disabled = false;
	slider.disabled = true;
    estado = 'Trabajo';
    estadoElement.textContent = estado;
}

function stopTimer() {
    clearInterval(intervalId);
    estado = 'Pausado';
    estadoElement.textContent = estado;

    // Deshabilitar los botones de iniciar y reiniciar
    startButton.disabled = false;
	stopButton.disabled = true;
    resetButton.disabled = false;
	slider.disabled = true;
}

function resetTimer() {
    clearInterval(intervalId);
    currentSeconds = totalSeconds;
    updateDisplay();

    startButton.disabled = false;
	stopButton.disabled = true;
    resetButton.disabled = true;
	slider.disabled = false;
	startButton.textContent = "Iniciar";
    estado = 'Preparado';
    estadoElement.textContent = estado;
}

slider.addEventListener('input', () => {
    const trabajo = parseInt(slider.value, 10);
    totalSeconds = (trabajo * 60) + prevSeconds;
    resetTimer(); // Reinicia el cronómetro con el nuevo valor
});

// Función para actualizar el valor del control deslizante
function updateSliderValue() {
    const currentMinutes = parseInt(slider.value);
    slider.value = currentMinutes; // Actualizar el valor visible
    const trabajo = parseInt(slider.value, 10);
    totalSeconds = (trabajo * 60) + prevSeconds;
    resetTimer(); // Reinicia el cronómetro con el nuevo valor
}

// Evento para el botón "+"
increaseButton.addEventListener('click', () => {
    const currentMinutes = parseInt(slider.value);
    if (currentMinutes < 60) {
        slider.value = currentMinutes + 1;
        updateSliderValue(); // Actualizar el control deslizante
    }
});

// Evento para el botón "-"
decreaseButton.addEventListener('click', () => {
    const currentMinutes = parseInt(slider.value);
    if (currentMinutes > 1) {
        slider.value = currentMinutes - 1;
        updateSliderValue(); // Actualizar el control deslizante
    }
});

startButton.addEventListener('click', startTimer);
stopButton.addEventListener('click', stopTimer);
resetButton.addEventListener('click', resetTimer);